import React, { useState, useEffect } from 'react';
import './App.css';
import Post from './Post'
import { db } from './firebase';

function App() {
  const [posts, setPosts] = useState([
    {
      username: "DW",
      caption: "Cap of DW",
      imageUrl: "https://oceandrive.com/get/files/image/galleries/dwayne-wade.jpg"
    },
    {
      username: "LBJ",
      caption: "Cap of LBJ",
      imageUrl: "https://pyxis.nymag.com/v1/imgs/847/0f7/504c63a03d8a751a5cbeda0bc064306bb4-lebron-james.rsquare.w1200.jpg"
    },
    {
      username: "KB",
      caption: "Cap of KB",
      imageUrl: "https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cfl_progressive%2Cq_auto:good%2Cw_1200/MTgwMzQyOTk2OTY3ODkyODQ0/gettyimages-490703338.jpg"

    },

  ]);

  useEffect(() => {
    db.collection("posts").onSnapshot(snapshot =>{
      setPosts(snapshot.docs.map((doc) => ({ id: doc.id, post: doc.data() })))
    })
  }, []);

  return (
    <div className="app">
      <div className="app__header">
        <img
          className="app_headerImage"
          // src= "https://cdn.icon-icons.com/icons2/2716/PNG/512/instagram_logo_icon_173070.png"
          src="https://1000logos.net/wp-content/uploads/2017/02/Instagram-Logo.png"
          alt=""
        />
      </div>
      <h1>hello, let's start!</h1>

      {
        posts.map(post => (
          <Post username={post.username}
            caption={post.caption}
            imageUrl={post.imageUrl}
          />
        ))
      }

      {/* <Post username="LBJ" caption="Cap of LBJ" imageUrl="https://oceandrive.com/get/files/image/galleries/dwayne-wade.jpg" />
      <Post username="DW" caption="Cap of DW" imageUrl="https://upload.wikimedia.org/wikipedia/commons/c/cf/LeBron_James_crop.jpg" />
      <Post username="TC" caption="Cap of TC" /> */}

    </div>
  );
}

export default App;
